<template>
  <input type="file" @change="changeHandle" accept=".docx"/>
  <vue-office-docx
    :src="base64Docx"
    @rendered="renderedHandler"
    @error="errorHandler"
  />
</template>

<script>
//引入VueOfficeDocx组件
import VueOfficeDocx from "@vue-office/docx";
//引入相关样式
import "@vue-office/docx/lib/index.css";
import { ref } from "vue";
import docx4js from "docx4js";

export default {
  components: {
    VueOfficeDocx,
  },
  setup() {
    const doC = ref(
      "https://lichee-img.oss-cn-hangzhou.aliyuncs.com/csdn/wenjian/doc%E6%96%87%E6%A1%A3.doc"
    );
    const doCx = ref(
      "https://lichee-img.oss-cn-hangzhou.aliyuncs.com/csdn/wenjian/docx%E6%96%87%E6%A1%A3.docx"
    );
    console.log(doC.value, doCx.value);
    const base64Doc = ref(
      `data:application/msword;base64,`
    );
    const base64Docx = ref(
      `data:application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;base64,`
    );
    const renderedHandler = () => {
      console.log("渲染完成");
      console.log(base64Docx.value);
    };
    const errorHandler = () => {
      console.log("渲染失败", base64Doc.value);
    };
    const changeHandle = (e) => {
      const file = e.target.files[0];
      const reader = new FileReader();
      reader.readAsDataURL(file);
      if (file.type === "application/msword") {
        const fs = require("fs");
        reader.onload = (e) => {
          const fileBuffer = e.target.result;
          // 将doc文件转换为docx
          docx4js
            .convert(fileBuffer, { convertTo: "docx" })
            .then(function (result) {
              console.log("新文件", result);
              // 将结果写入一个新的docx文件
              fs.writeFileSync("path/to/your/new/docx/file.docx", result);
            });
        };
      } else if (
        file.type ===
        "application/vnd.openxmlformats-officedocument.wordprocessingml.document"
      ) {
        reader.onload = (e) => {
          base64Docx.value = e.target.result;
        };
      } else {
        console.log("文件类型不符合");
      }
    };

    return {
      base64Docx,
      renderedHandler,
      errorHandler,
      changeHandle,
    };
  },
};
</script>
